@bot.message_handler(content_types=['text'])
def main_func(message):
    if message.text == '/stress':
       bot.send_message(message.chat.id, stress[0], parse_mode='html', reply_markup=get_kb(0, name='stress'))
    elif message.text == '/reasons':
       bot.send_message(message.chat.id, reasons, parse_mode='html', reply_markup=get_kb(0, name='reasons'))
    elif message.text == '/methods':
       bot.send_message(message.chat.id, methods[0], parse_mode='html', reply_markup=get_kb(0, name='methods'))
    elif message.text == '/facts':
       bot.send_message(message.chat.id, facts[0], parse_mode='html', reply_markup=get_kb(0, name='facts'))
    else:
        img = open('assets/meme.jpg', 'rb')
        bot.send_photo(message.chat.id, img, '–Ø –Ω–µ –ø–æ–Ω–∏–º–∞—é –í–∞—Å. –í–≤–µ–¥–∏—Ç–µ –æ–¥–Ω—É –∏–∑ —Å—É—â–µ—Å—Ç–≤—É—é—â–∏—Ö –∫–æ–º–∞–Ω–¥, –ø–æ–∂–∞–ª—É–π—Å—Ç–∞: /stress, /methods, /reasons, /facts.')
        img.close()

def get_kb(index, name):
    kb = types.InlineKeyboardMarkup(row_width=2)
    if name == 'stress':
     if index == 0:
        btn_next = types.InlineKeyboardButton('–î–∞–ª–µ–µ', callback_data='stress_1')
        kb.add(btn_next)
     elif index > 0 and index + 1 < len(stress):
        btn_prev = types.InlineKeyboardButton('–ù–∞–∑–∞–¥', callback_data=f'stress_{index - 1}')
        btn_next = types.InlineKeyboardButton('–î–∞–ª–µ–µ', callback_data=f'stress_{index + 1}')
        kb.add(btn_next, btn_prev)
     else:
        btn_prev = types.InlineKeyboardButton('–ù–∞–∑–∞–¥', callback_data=f'stress_{index - 1}')
        btn_to_reasons = types.InlineKeyboardButton('–ü–µ—Ä–µ–π—Ç–∏ –∫ –ø—Ä–∏—á–∏–Ω–∞–º', callback_data='to_reasons')
        kb.add(btn_prev)
        kb.add(btn_to_reasons)

    elif name == 'reasons':
        btn_to_methods = types.InlineKeyboardButton('–ü–µ—Ä–µ–π—Ç–∏ –∫ –º–µ—Ç–æ–¥–∞–º', callback_data='to_methods')
        kb.add(btn_to_methods)

    elif name == 'methods':
     btn_to_facts = types.InlineKeyboardButton('–ü–µ—Ä–µ–π—Ç–∏ –∫ —Ñ–∞–∫—Ç–∞–º', callback_data='to_facts')
     if index == 0:
        btn_next = types.InlineKeyboardButton('–î–∞–ª–µ–µ', callback_data='methods_1')
        kb.add(btn_next)
     elif index > 0 and index + 1 < len(methods):
        btn_prev = types.InlineKeyboardButton('–ù–∞–∑–∞–¥', callback_data=f'methods_{index - 1}')
        btn_next = types.InlineKeyboardButton('–î–∞–ª–µ–µ', callback_data=f'methods_{index + 1}')
        kb.add(btn_next, btn_prev)
     else:
        btn_prev = types.InlineKeyboardButton('–ù–∞–∑–∞–¥', callback_data=f'methods_{index - 1}')
        kb.add(btn_prev)
        kb.add(btn_to_facts)


    elif name == 'facts':
     btn_to_rate = types.InlineKeyboardButton('–ü–µ—Ä–µ–π—Ç–∏ –∫ –æ—Ü–µ–Ω–∫–µ',callback_data='to_rate')
     if index == 0:
        btn_next = types.InlineKeyboardButton('–î–∞–ª–µ–µ', callback_data='facts_1')
        kb.add(btn_next)
     elif index > 0 and index + 1 < len(facts):
        btn_prev = types.InlineKeyboardButton('–ù–∞–∑–∞–¥', callback_data=f'facts_{index - 1}')
        btn_next = types.InlineKeyboardButton('–î–∞–ª–µ–µ', callback_data=f'facts_{index + 1}')
        kb.add(btn_next, btn_prev)
     else:
        btn_prev = types.InlineKeyboardButton('–ù–∞–∑–∞–¥', callback_data=f'facts_{index - 1}')
        kb.add(btn_prev)
        kb.add(btn_to_rate)

    return kb

def rate_kb():
    rate = types.InlineKeyboardMarkup(row_width=5)
    btn_1 = types.InlineKeyboardButton('üåü', callback_data='rate_one')
    btn_2 = types.InlineKeyboardButton('üåüüåü', callback_data='rate_two')
    btn_3 = types.InlineKeyboardButton('üåüüåüüåü', callback_data='rate_three')
    btn_4 = types.InlineKeyboardButton('üåüüåüüåüüåü', callback_data='rate_four')
    btn_5 = types.InlineKeyboardButton('üåüüåüüåüüåüüåü', callback_data='rate_five')
    rate.add(btn_1, btn_2, btn_3, btn_4, btn_5)
    return rate

@bot.callback_query_handler(func=lambda call: True)
def main(call):
    if call.data.startswith('stress'):
        call_stress(call)
    elif call.data.startswith('methods'):
        call_methods(call)
    elif call.data.startswith('facts'):
        call_facts(call)
    if call.data.startswith('rate'):
        call_rate(call)


    if 'to' in call.data:
        if call.data.endswith('_reasons'):
            bot.edit_message_text(reasons, chat_id=call.message.chat.id, message_id=call.message.message_id, reply_markup=get_kb(index=1, name='reasons'))
        elif call.data.endswith('_methods'):
            bot.edit_message_text(methods[0], chat_id=call.message.chat.id, message_id=call.message.message_id, reply_markup=get_kb(index=0, name='methods'))
        elif call.data.endswith('_facts'):
            bot.edit_message_text(facts[0], chat_id=call.message.chat.id, message_id=call.message.message_id, reply_markup=get_kb(index=0, name='facts'))
        elif call.data.endswith('_rate'):
            bot.edit_message_text('–°–ø–∞—Å–∏–±–æ, —á—Ç–æ –¥–æ—á–∏—Ç–∞–ª–∏ –¥–æ –∫–æ–Ω—Ü–∞. \n–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –æ—Ü–µ–Ω–∏—Ç–µ –±–æ—Ç–∞ (–æ—á–µ–Ω—å –≤–∞–∂–Ω–æ –¥–ª—è –¥–∞–ª—å–Ω–µ–π—à–µ–≥–æ —Ä–∞–∑–≤–∏—Ç–∏—è)', chat_id=call.message.chat.id, message_id=call.message.message_id, reply_markup=rate_kb())



def call_stress(call):
    index = int(call.data.split('_')[1])
    message_id = call.message.message_id
    chat_id = call.message.chat.id
    bot.edit_message_text(stress[index], chat_id=chat_id, message_id=message_id, reply_markup=get_kb(index, name='stress'))

